import { saveToLocalStorage } from '../../utils/storage';

// Fetch profile (in this case, just using the auth user data)
export const fetchProfile = (userId) => async (dispatch, getState) => {
  dispatch({ type: 'FETCH_PROFILE_REQUEST' });
  
  try {
    const { auth } = getState();
    if (auth.user && auth.user.id === userId) {
      dispatch({
        type: 'FETCH_PROFILE_SUCCESS',
        payload: { profileData: auth.user },
      });
    } else {
      throw new Error('User not found');
    }
  } catch (error) {
    dispatch({
      type: 'PROFILE_ERROR',
      payload: { error: error.message },
    });
  }
};

// Update profile
export const updateProfile = (profileData) => async (dispatch, getState) => {
  dispatch({ type: 'FETCH_PROFILE_REQUEST' });
  
  try {
    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 500));
    
    const { auth } = getState();
    if (auth.user) {
      const updatedUser = { ...auth.user, ...profileData };
      
      saveToLocalStorage('user', updatedUser);
      
      dispatch({
        type: 'UPDATE_PROFILE_SUCCESS',
        payload: { profileData: updatedUser },
      });
    } else {
      throw new Error('Not authenticated');
    }
  } catch (error) {
    dispatch({
      type: 'PROFILE_ERROR',
      payload: { error: error.message },
    });
    throw error;
  }
};
