import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProfile, updateProfile } from '../store/actions/profileActions';
import ProfileForm from '../components/Profile/ProfileForm';

const ProfilePage = () => {
  const dispatch = useDispatch();
  const { user } = useSelector(state => state.auth);
  const { profileData, loading, error } = useSelector(state => state.profile);

  useEffect(() => {
    if (user) {
      dispatch(fetchProfile(user.id));
    }
  }, [dispatch, user]);

  const handleUpdateProfile = (updatedData) => {
    return dispatch(updateProfile(updatedData));
  };

  if (loading && !profileData) return <div>Loading profile...</div>;

  return (
    <div className="profile-page">
      <h2>Your Profile</h2>
      {error && <div className="error-message">{error}</div>}
      <ProfileForm 
        profileData={profileData || user} 
        onUpdate={handleUpdateProfile} 
        loading={loading}
      />
    </div>
  );
};

export default ProfilePage;
