const initialState = {
    user: null,
    isAuthenticated: false,
    loading: false,
    error: null,
    lastLoginAttempt: null
  };
  
  export default function authReducer(state = initialState, action) {
    switch (action.type) {
      case 'LOGIN_REQUEST':
        return { 
          ...state, 
          loading: true, 
          error: null,
          lastLoginAttempt: new Date().toISOString()
        };
      
      case 'LOGIN_SUCCESS':
        return {
          ...state,
          user: action.payload.user,
          isAuthenticated: true,
          loading: false,
          error: null
        };
      
      case 'LOGIN_FAILURE':
        return {
          ...state,
          user: null,
          isAuthenticated: false,
          loading: false,
          error: action.payload
        };
      
      case 'LOGOUT_SUCCESS':
        return initialState;
      
      default:
        return state;
    }
  }
