// src/components/Todo/TodoList.jsx
import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { loadTodos, setTodoFilter } from '../../store/actions/todoActions';
import TodoItem from './TodoItem';

const TodoList = () => {
  const { todos, loading, error, filter } = useSelector(state => state.todos);
  const { user } = useSelector(state => state.auth);
  const dispatch = useDispatch();

  useEffect(() => {
    if (user) {
      dispatch(loadTodos(user.username));
    }
  }, [dispatch, user]);

  const filteredTodos = todos.filter(todo => {
    if (filter === 'completed') return todo.completed;
    if (filter === 'active') return !todo.completed;
    return true;
  });

  if (loading) return <div>Loading todos...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="todo-list">
      <div className="todo-filters">
        <button
          onClick={() => dispatch(setTodoFilter('all'))}
          className={filter === 'all' ? 'active' : ''}
        >
          All
        </button>
        <button
          onClick={() => dispatch(setTodoFilter('active'))}
          className={filter === 'active' ? 'active' : ''}
        >
          Active
        </button>
        <button
          onClick={() => dispatch(setTodoFilter('completed'))}
          className={filter === 'completed' ? 'active' : ''}
        >
          Completed
        </button>
      </div>
      
      {filteredTodos.length === 0 ? (
        <div className="no-todos">No todos found</div>
      ) : (
        <ul className="todos">
          {filteredTodos.map(todo => (
            <TodoItem key={todo.id} todo={todo} />
          ))}
        </ul>
      )}
    </div>
  );
};

export default TodoList;
