// src/store/reducers/authReducer.js
const initialState = {
  user: null,
  isAuthenticated: false
};

export default function authReducer(state = initialState, action) {
  switch (action.type) {
    case 'LOGIN_SUCCESS':
      return {
        ...state,
        user: action.payload.user,
        isAuthenticated: true
      };
    case 'LOGOUT_SUCCESS':
      return initialState;
    default:
      return state;
  }
}
